import React from "react";
import avatar from "../assets/profile.png";
import {Link} from "react-router-dom"
import {useFormik} from 'formik';
import {Toaster} from 'react-hot-toast';
import {validatepassword} from "../helper/validate";





export default function Recovery() {

  const formik=useFormik({
       initialValues:
       {
         password:''
       }, 
       validateOnBlur:false,
       validateOnChange:false,
       validate:validatepassword,

       onSubmit:async values=>{
        console.log(values)
       }


  })

  return (
    <div>
     <Toaster position="top-center" reverseOrder={false}></Toaster>
     <div className="container-well mt-5 ">
      <div className="row justify-content-center  ">

        <h3 className="col-2 mt-10">Recovery</h3>
      </div>
      <div className="row justify-content-center">
        <p className="col-2.5">Enter OTP to recover</p>
      </div>

      <div className="row justify-content-center ">


      </div>
      <form onSubmit={formik.handleSubmit}>
      <span className="row justify-content-center">Enter 6 digit otp to recover</span>
      <div className="row justify-content-center " onSubmit={formik.handleSubmit}>
          <input className="text" style={{marginTop:10}} type="text" name="password" onChange={formik.handleChange}  placeholder="otp" ></input>

        </div>
        <div  className="row justify-content-center  ">
          <button  type='submit' style={{marginTop:10}} onSubmit={formik.handleSubmit} className="btn btn-success">Let's go</button>
        </div>
        <div className="row justify-content-center  ">
          <p>Can't get otp? <button className="text-danger">Resend</button></p>
        </div>

        </form>
    </div>
    </div>
  );
}
